/*Table structure for table `wp_posts` */

DROP TABLE IF EXISTS `wp_posts`;

CREATE TABLE `wp_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(20) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=74 DEFAULT CHARSET=utf8;

/*Data for the table `wp_posts` */

insert  into `wp_posts`(`ID`,`post_author`,`post_date`,`post_date_gmt`,`post_content`,`post_title`,`post_excerpt`,`post_status`,`comment_status`,`ping_status`,`post_password`,`post_name`,`to_ping`,`pinged`,`post_modified`,`post_modified_gmt`,`post_content_filtered`,`post_parent`,`guid`,`menu_order`,`post_type`,`post_mime_type`,`comment_count`) values (1,1,'2008-08-12 10:00:00','2008-08-12 10:00:00','<p><span><span style=\"font-family: tahoma; font-size: 100%;\"><img src=\"http://www.deptan.go.id/news/admin/image/kln_news.jpg\" border=\"0\" width=\"374\" height=\"204\" /></span></span></p> <p style=\"text-align: justify;\"><span><span style=\"font-family: tahoma; font-size: 100%;\">”Pada dasarnya kita memilih untuk tidak bergantung dengan negara lain dan Pinjaman dan Hibah Luar Negeri menjadi sebuah alternatif untuk mengurangi gap antara ketersediaan dana dalam negeri dengan dana investasi yang dibutuhkan bagi pembangunan”. Hal itu disampaikan dalam sambutan Sekretaris Jenderal Kementerian Pertanian pada seminar Pemanfaatan Peluang Kerjasama Luar Negeri yang diselenggarakan pada tanggal 4 Maret 2010 di Hotel Permata, Bogor.<br /><br />Sekjen juga memberikan arahan agar pembangunan di Indonesia dapat merata sampai ke daerah-daerah terpencil, sektor pertanian ke depan memiliki prioritas di wilayah timur Indonesia, memperhatikan prinsip kelestarian lingkungan hidup dan diversifikasi pangan.  Kebijakan sektor pertanian harus pro rakyat miskin, minimal mengurangi gap antara si kaya dan si miskin.<br /><br />Pada acara yang diselenggarakan oleh Biro Kerjasama Luar Negeri (KLN), Kementerian Pertanian ini, dihadirkan pembicara dari:Australian Centre for International Agricultural Research (ACIAR): Julien De Meyer, ACIAR Country Manager dan Mirah Nuryati, Stakeholder Relationship Manager,Asisten Kepala Perwakilan The Food and Agriculture Organization (FAO): Dr. Benni H. Sormin, JICA Indonesia Office: Dinur Krismasari, JICA Senior Representative dan Hayato Nakajima, JICA Agriculture Policy Advisor dengan moderator Ir. Farid Hasan Baktir MEc, Kepala Biro KLN. Hadir pada pertemuan tersebut para pejabat dan staf lingkup Kementerian Pertanian, Dinas Pertanian Provinsi Sumatera Utara dan Nusa Tenggara Barat. <br />Para Narasumber menjelaskan mengenai peluang kerjasama yang dapat dimanfaatkan, prosedur dan persyaratan yang dibutuhkan dalam pengusulan proposal kerjasama dengan ketiga Lembaga Internasional tersebut.<br /><br />Program ACIAR yang dapat dimanfaatkan antara lain: Beasiswa John Allwright yaitu kesempatan melanjutkan studi jenjang S2 dan/atau S3 di Australia, Beasiswa John Dillon yang memberikan pelatihan kepemimpinan bagi Manager Madya, serta pelatihan yang terkait program dalam suatu proyek.<br /><br />Sedangkan FAO menegaskan kembali bahwa Keberadaan Indonesia di dunia internasional telah naik tingkat, bukan sebagai negara prioritas yang perlu dibantu lagi tetapi sudah waktunya untuk membantu negara lain.  Selanjutnya FAO menjelaskan peluang bantuan yang dapat dimanfaatkan dari FAO antara lain: Technical Cooperation Program (TCP), Telefood dan TCP Facility (TCP-F).<br /><br />Diantara empat program prioritas utama JICA di Indonesia, kegiatan di sektor Pertanian termasuk dalam program Stabilnya Ketersediaan Pangan dan Peningkatan Pendapatan di Pedesaan yang merupakan isu Poverty Reduction dengan bentuk kegiatan Grant Aid, Loan Aid dan Technical Cooperation (Policy Advisor Dispatch, Technical Cooperation Project, Technical Cooperation Development Planning dan Training).<br /> <br /><strong>Sumber: bagian PBB untuk p</strong></span></span><strong><span><span style=\"font-family: tahoma; font-size: x-small;\">angan dan pertanian/KLN/Setjen</span></span></strong></p> <p>Bookmark</p> <!-- AddThis Button BEGIN --> <div class=\"addthis_toolbox addthis_default_style \"><a class=\"addthis_button_preferred_1\"></a> <a class=\"addthis_button_preferred_2\"></a> <a class=\"addthis_button_preferred_3\"></a> <a class=\"addthis_button_preferred_4\